public static void main(String[] args) { 	Scanner input = new Scanner(System.in); 	long millis; 	String output; 	System.out.println("Enter milliseconds: "); 	millis = input.nextLong(); 	output = convertMillis(millis); 	System.out.println(output); } public static String convertMillis(long millis) { 	 	long sec, min, hrs; 	sec = millis/1000; 	min = sec/60; 	hrs = min/60; 	return(hrs + ":" + min + ":" + sec); }